select * from(
    select /*+INDEX_DESC(table_drink SYS_IL0000020594C00006$$)*/ rownum as rn, drinkId, drinkName,
        (select cateName from table_drinkCate where table_drink.cateCode = table_drinkCate.cateCode) cateName
    from table_drink 
    where 




	    drinkName like '%' || #{keyword} || '%' and




    rownum <= #{pageNum} * #{amount} 
    )
where rn > (#{pageNum} -1) * #{amount}
